mapper.select:
select * from dbhsm_task_queue

    and task_id=#{taskId}
    and table_id = #{tableId}
    and enc_status=#{encStatus}
    and dec_status=#{decStatus}
    and create_time=#{createTime}
    and create_by=#{createBy}
    and update_time=#{updateTime}
    and update_by=#{updateBy}

mapper.insert:
insert into dbhsm_task_queue ( task_id
                             , table_id
                             , enc_status
                             , dec_status
                             , create_time
                             , create_by
                             , update_time
                             , update_by)
values ( #{taskId}
       , #{tableId}
       , #{encStatus}
       , #{decStatus}
       , #{createTime}
       , #{createBy}
       , #{updateTime}
       , #{updateBy})

mapper.update:
update dbhsm_task_queue

    task_id=#{taskId}
    ,table_id = #{tableId}
    ,enc_status = #{encStatus}
    ,dec_status = #{decStatus}
    ,create_time = #{createTime}
    ,create_by = #{createBy}
    ,update_time = #{updateTime}
    ,update_by = #{updateBy}


    and task_id=#{taskId}
    and table_id=#{tableId}
    and enc_status=#{encStatus}
    and dec_status=#{decStatus}
    and create_time=#{createTime}
    and create_by=#{createBy}
    and update_time=#{updateTime}
    and update_by=#{updateBy}

mapper.select:
select ins.id as dbInstanceId, task.task_id as taskId, ins.database_capital_name as databaseCapitalName, ins.database_dba as databaseDba, ins.database_type as databaseType, ins.database_server_name as databaseServerName, ins.database_edition as databaseEdition, ins.database_ip as databaseIp, ins.database_port as databasePort, tab.table_id as tableId, tab.table_name as tableName,
GROUP_CONCAT(case when encryption_status = 2 then encrypt_columns end) as finishedColumns, GROUP_CONCAT(case when encryption_status in (0,1) then encrypt_columns end) as unfinishedColumns,
task.enc_status as encStatus, task.dec_status as decStatus, tab.table_status as tableStatus, tab.thread_count as threadCount, tab.batch_count as batchCount
,GROUP_CONCAT(col.encrypt_columns) as unfinishedColumns
from dbhsm_encrypt_columns col left join dbhsm_encrypt_table tab on tab.table_id = col.table_id left join dbhsm_db_instance ins on ins.id = tab.instance_id left join dbhsm_task_queue task on task.table_id = tab.table_id
and task.enc_status is not null and task.enc_status=#{status}
and task.dec_status is not null and task.dec_status=#{status}
and ins.database_capital_name=#{databaseCapitalName}
and ins.database_server_name=#{databaseServerName}
and tab.table_name=#{tableName}
group by task.task_id

select task.task_id, col.encrypt_columns, col.encryption_status, col.encryption_algorithm, col.ethernet_port, col.columns_type, col.secret_key_id, col.db_table, col.db_instance_id from dbhsm_encrypt_columns col left join dbhsm_encrypt_table tab on tab.table_id = col.table_id left join dbhsm_db_instance ins on ins.id = tab.instance_id left join dbhsm_task_queue task on task.table_id = tab.table_id where col.table_id = #{tableId}
and task.enc_status is not null
and task.dec_status is not null
